$.fn.toggleHTML = function (a, b) {
  return this.html(function (_, html) {
    return $.trim(html) === a ? b : a;
  });
};
export const layout = function () {
  var handleScroll = function () {
    $('#menu_toggle').click(function () {
      $('#sidebar_togg').toggleClass('nav-sm');
    });
  };

  var handleshowHide = function () {
    $('.show_details').click(function () {
      $('.loan_fields .loan_label , .pay_info_wrap , .review_note').slideToggle();
      $('.show_details').toggleHTML('<i class="fa fa-angle-double-up"></i> Hide Details', '<i class="fa fa-angle-double-down"></i> Show Details');
    });
  };
// $(document).on('keyup', 'input', function() {
//         if($(this).val()) {
//                 $(this).addClass('inputfill');
//             }else{
//                 $(this).removeClass('inputfill');
//             }
//         });
  return {
    //main function to initiate the module
    init: function () {
      handleScroll();
      handleshowHide();
    }
  };
}();

